import React from "react";
import ProductCard from "./ProductCard";
import "../css/ProductGrid.css";

class ProductGrid extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      subtotal: this.props.subtotal
    };
  }


  render() {
    return (
      <div className="container">
        {this.props.list.map(item => <ProductCard name={item.name} price={item.price} size={item.size} light={item.light} img={item.img} isInCart={this.props.isInCart} onSubmit={this.props.onSubmit} quantity={this.props.isInCart ? item.quantity : 1} />)}
      </div>
    )
  }
}

export default ProductGrid;
