import React from "react";

/**
 * This class maps each item in your products list to individual Product Cards. 
 */
class ProductGrid extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
    };
  }

  /**
   * TODO: Create a new .js class that styles each item, then map each item from this.props.list from Shop to a Product Card.
   *
   */
  productGrid() {
    const grid = this.props.list.map(item => {
      return <li key={item.name} isInCart={this.props.isInCart}>{item.name}, <strong>{item.size}</strong> </li>
    });
    return grid;
  }

  render() {
    return (
      <div>
        {this.productGrid()}
      </div>
    )
  }
}

export default ProductGrid;
