import React from "react";
import "../css/Shop.css";
import ProductGrid from "./ProductGrid";
import Cart from "./Cart";
import Header from "./Header"

class Shop extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      cartContents: [],
      size: "Size",
      light: "Light",
      sortPrice: undefined,
      oldTotal: 0
    };
  }

  onSelectFilterSize = event => {
    this.setState({size:event})
  };

  onSelectFilterLight = event => {
    this.setState({light:event})
  };


  matchesFilterSize = item => {
    if (this.state.size === "Size"){
      return true
    } else if (this.state.size === item.size){
      return true
    } else {
      return false
    }
  }

  matchesFilterLight = item => {
    if (this.state.light === "Light"){
      return true
    } else if (this.state.light === item.light){
      return true
    } else {
      return false
    }
  }

  // checks if each item matches all the filters, returning true if it does
  filterItems = item => {
    return this.matchesFilterSize(item) && this.matchesFilterLight(item);
  }

  sortLowToHigh = (a,b) => (a.price > b.price)? 1 : -1
  sortHighToLow = (a,b) => (a.price < b.price)? 1 : -1

  onPriceLowToHigh = () => {
    this.setState({sortPrice:this.sortLowToHigh});
  }

  onPriceHighToLow = () => {
    this.setState({sortPrice:this.sortHighToLow});

  }

  onPriceSelect = () => {
    this.setState({sortPrice:undefined});
  }

  addToCart = (item) => {
    if (this.state.cartContents.some(el => item.name === el.name)) {
      let index = this.state.cartContents.findIndex(el => el.name === item.name);
      let updatedContents = [...this.state.cartContents];
      updatedContents[index] = { ...updatedContents[index], quantity: parseFloat(updatedContents[index].quantity) + parseFloat(item.quantity) }
      this.setState({ cartContents: updatedContents });
    } else {
      this.setState(state => ({
        cartContents: [...state.cartContents, item]
      }))
    }
    {console.log("newest cart" + this.state.cartContents)}
    this.calcSubtotal();
  }

  removeFromCart = (item) => {
    this.setState(state => ({
      cartContents: state.cartContents.filter(el => el.name !== item.name)
    }))
  }

  onDecrease = (item) => {

    if (this.state.cartContents.some(el => item.name === el.name)) {
      let index = this.state.cartContents.findIndex(el => el.name === item.name);
      let updatedContents = [...this.state.cartContents];
      updatedContents[index] = { ...updatedContents[index], quantity: parseFloat(updatedContents[index].quantity) -1 + parseFloat(item.quantity) }
      this.setState({ cartContents: updatedContents });
    } else {
      this.setState(state => ({
        cartContents: [...state.cartContents, item]
      }))
    }
  }

  onIncrease = (item) => {

    if (this.state.cartContents.some(el => item.name === el.name)) {
      let index = this.state.cartContents.findIndex(el => el.name === item.name);
      let updatedContents = [...this.state.cartContents];
      updatedContents[index] = { ...updatedContents[index], quantity: parseFloat(updatedContents[index].quantity) +1 + parseFloat(item.quantity) }
      // {console.log(updatedContents[index].quantity)}
      this.setState({ cartContents: updatedContents });
    } else {
      this.setState(state => ({
        cartContents: [...state.cartContents, item]
      }))
    }
  }


  calcSubtotal = () => {
    {console.log(this.state.cartContents[0])}
    let newTotal = 0;
    for (let i=0; i<this.state.cartContents.length; i++) {
      newTotal = this.state.cartContents.reduce((prev, curr) => prev + (curr[i].quantity*curr[i].price), 0);
    }
    {console.log("TOTAL" + newTotal)}
    this.setState({oldTotal: newTotal});
  }


  render() {
    return (
      <div className="wrapper">
        <div className="cards-container">
        <Header onSelectFilterSize={this.onSelectFilterSize} onSelectFilterLight={this.onSelectFilterLight} onPriceLowToHigh={this.onPriceLowToHigh} onPriceHighToLow={this.onPriceHighToLow} onPriceSelect={this.onPriceSelect} size={this.state.size} light={this.state.light}/>

        {this.props.list.filter(this.filterItems).length > 0 ? 
          <div>
            <ProductGrid list={this.props.list.filter(this.filterItems).sort(this.state.sortPrice)} isInCart={false} onSubmit={this.addToCart}onIncrease={this.onIncrease} onDecrease={this.onDecrease}/>
          </div>
          :
          <div>
            <p>No results match the filters :(</p>
          </div>
          }
      </div>
      <div className="cart-container">
          <Cart contents={this.state.cartContents} subtotal={this.state.oldTotal} onSubmit={this.removeFromCart} />
      </div>
      </div>
    );
  }
}

export default Shop;
