import React from "react";
import ProductCard from "./ProductCard";
import "../css/ProductGrid.css";

/**
 * This class maps each item in the list of plants to individual Product Cards. 
 */
class ProductGrid extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
    };
  }

  productList() {
    const grid = this.props.list.map(item => {
      return <ProductCard key={item.name} name={item.name} price={item.price} size={item.size} light={item.light} img={item.img} isInCart={this.props.isInCart} onSubmit={this.props.onSubmit} onIncrease={this.props.onIncrease} onDecrease={this.props.onDecrease}
        // If the item is in the cart, then it keeps its quantity; else, it's quantity is one. 
        quantity={this.props.isInCart ? item.quantity : 1} />
    });
    return grid;
  }

  render() {
    return (
      <div className="container">
        {this.productList()}
      </div>
    )
  }
}

export default ProductGrid;
