import React from "react";
import "../css/Shop.css";
import ProductGrid from "./ProductGrid";
import Cart from "./Cart";
import Header from "./Header"

class Shop extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      cartContents: [],
      size: "Size",
      light: "Light",
      sortPrice: undefined,
      totalPrice: 0
    };
  }

  onSelectFilterSize = event => {
    this.setState({size:event})
  };

  onSelectFilterLight = event => {
    this.setState({light:event})
  };


  matchesFilterSize = item => {
    if (this.state.size === "Size"){
      return true
    } else if (this.state.size === item.size){
      return true
    } else {
      return false
    }
  }

  matchesFilterLight = item => {
    if (this.state.light === "Light"){
      return true
    } else if (this.state.light === item.light){
      return true
    } else {
      return false
    }
  }

  // checks if each item matches all the filters, returning true if it does
  filterItems = item => {
    return this.matchesFilterSize(item) && this.matchesFilterLight(item);
  }

  sortAsc = (a,b) => (a-b)? 1 : -1
  sortDesc = (a,b) => (b-a)? 1 : -1

  // sortByPrice = key => {
  //   this.setState({price})
  //   if (this.state.price === undefined) {
  //     return true 
  //   } else if (this.state.price === "LowToHigh") {
  //     this.setState({price:this.sortAsc})
  //   } else if (this.state.price === "HighToLow") {
  //     this.setState({price:this.sortDesc})
  //   } else {
  //     this.setState({price:undefined})
  //   }
  // }

  onPriceLowToHigh = () => {
    this.setState({sortPrice:this.sortAsc});
  }

  onPriceHighToLow = () => {
    this.setState({sortPrice:this.sortDesc});
  }

  onPriceSelect = () => {
    this.setState({sortPrice:undefined});
  }

  addToCart = (item) => {
    if (this.state.cartContents.some(el => item.name === el.name)) {
      let index = this.state.cartContents.findIndex(el => el.name === item.name);
      let updatedContents = [...this.state.cartContents];
      updatedContents[index] = { ...updatedContents[index], quantity: parseFloat(updatedContents[index].quantity) + parseFloat(item.quantity) }
      this.setState({ cartContents: updatedContents });
    } else {
      this.setState(state => ({
        cartContents: [...state.cartContents, item]
      }))
    }
  }

  removeFromCart = (item) => {
    this.setState(state => ({
      cartContents: state.cartContents.filter(el => el.name !== item.name)
    }))
  }

  calcSubtotal = () => {

    var productSum = 0;

    subtotalArray = [cartContents.length];
    for (var i=0; i< cartContents.length; i++) {
      for (var j=0; i<subtotalArray.length; i++) {
        subtotal[j].ap
        productSum
      }
      productSum += this.state.cartContents[i].quantity * this.state.cartContents[i].price
    }

  }


  render() {
    // console.log(this.props.list.filter(this.filterItems).sort(this.state.price))
    return (
      <div className="wrapper">
        <div className="cards-container">
        <Header onSelectFilterSize={this.onSelectFilterSize} onSelectFilterLight={this.onSelectFilterLight} onPriceLowToHigh={this.onPriceLowToHigh} onPriceHighToLow={this.onPriceHighToLow} onPriceSelect={this.onPriceSelect} size={this.state.size} light={this.state.light}/>

        {this.props.list.filter(this.filterItems).length > 0 ? 
          <div>
            <ProductGrid list={this.props.list.filter(this.filterItems).sort(this.state.price)} isInCart={false} onSubmit={this.addToCart}/>
          </div>
          :
          <div>
            <p>No results match the filters :(</p>
          </div>
          }
      </div>
      <div className="cart-container">
          <Cart contents={this.state.cartContents} subtotal={this.calcSubtotal} onSubmit={this.removeFromCart} />
      </div>
      </div>
    );
  }
}

export default Shop;
