import React from "react";
import "../css/Cart.css";
import { Button, Navbar, Form, FormControl} from "react-bootstrap";
import ProductGrid from "./ProductGrid";

class Cart extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      subtotal: this.props.subtotal
    };
  }

  onCheckout = () => {
    alert("Checkout Subtotal: $100");
  }


  render() {
    return (
      <div className="cart">
        <h3>Shopping Cart</h3>
        <ProductGrid isInCart list={this.props.contents} onSubmit={this.props.onSubmit} />
        {/* {this.props.contents.length > 0 ? this.props.contents.reduce((acc, item) => console.log(acc)) : `$0.00`} */}
        <Navbar className="bg-light justify-content-between" sticky="bottom">
          <Navbar.Brand>Subtotal: ${this.props.subtotal}</Navbar.Brand>
          <Button onClick={this.onCheckout}>Checkout</Button>
        </Navbar>
      </div>
    )
  }
}

export default Cart;
