import React from 'react';
import logo from './logo.svg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Cactus from "./assets/cactus.jpg";
import Shop from "./components/Shop";
import AgaveA from "./assets/Agave Americana Medio Picta Alba.jpg";
import AgaveL from "./assets/Agave Lophanta Quadricolor.jpg";
import Aloe from "./assets/Aloe Ferox.jpg";
import Astrophytum from "./assets/Astrophytum Mirostigma.jpg";
import Chamelobibia from "./assets/Chamelobibia Rose Quartz.jpg";
import Cleistocctus from "./assets/Cleistocctus Winteri Golden Rat Tail.jpg";
import Dwarf from "./assets/Dwarf Agave Stricta Hedgehog.jpg";
import Echinobivia from "./assets/Echinobivia Hybrid Rainbow Bursts.jpg";
import Echinocereus from "./assets/Echinocereus Pectinatus Rubispinus.jpg";
import Euphorbia from "./assets/Euphorbia Flanaganii Medusa's Head.jpg";
import Ferocactus from "./assets/Ferocactus Latispinus Devil's Tongue.jpg";
import Saguaro from "./assets/Saguaro Cordon Pachycereus Pringleite.jpg";



const ProductList = [
  { name: "Ferocactus Latispinus Devil's Tongue", price: 36.99, size: "Medium", light: "Medium Light", img: Ferocactus},
  { name: "Agave Lophanta Quadricolor", price: 69.99, size: "Large", light: "Medium Light", img: AgaveL},
  { name: "Echinobivia Hybrid Rainbow Bursts", price: 12.99, size: "Desktop", light: "Low Light", img: Echinobivia},
  { name: "Dwarf Agave Stricta Hedgehog", price: 46.99, size: "Medium", light: "Bright Light", img: Dwarf},
  { name: "Astrophytum Mirostigma", price: 12.99, size: "Desktop", light: "Low Light", img: Astrophytum},
  { name: "Chamelobibia Rose Quartz", price: 9.99, size: "Desktop", light: "Bright Light", img: Chamelobibia},
  { name: "Agave Americana Medio Picta Alba", price: 59.99, size: "Large", light: "Bright Light", img: AgaveA},
  { name: "Cleistocctus Winteri Golden Rat Tail", price: 9.99, size: "Desktop", light: "Medium Light", img: Cleistocctus},
  { name: "Euphorbia Flanaganii Medusa's Head", price: 26.99, size: "Medium", light: "Bright Light", img: Euphorbia},
  { name: "Saguaro Cordon Pachycereus Pringleite", price: 129.99, size: "Large", light: "Bright Light", img: Saguaro},
  { name: "Echinocereus Pectinatus Rubispinus", price: 19.99, size: "Desktop", light: "Medium Light", img: Echinocereus},
  { name: "Aloe Ferox", price: 36.99, size: "Medium", light: "Medium Light", img: Aloe},
]

function App() {
  return (
    <div className="app">
      <h1>CS1300 Cactus Shop</h1>
      <Shop list={ProductList} />
    </div>
  );
}

export default App;


