import React from "react";
import "../css/Cart.css";
import { Button, Navbar, Form, FormControl } from "react-bootstrap";
import ProductGrid from "./ProductGrid";

class Cart extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      contents: this.props.contents,
      subtotal: 0,
    };
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevProps.contents !== this.props.contents || prevState.contents !== this.state.contents) {
      this.setState({ contents: this.props.contents });
      this.setState({
        subtotal: this.props.contents.reduce((acc, curr) => {
          return acc + curr.quantity * curr.price;
        }, 0)
      });
    }
  }

  onCheckout = () => {
    alert("Checkout Subtotal: $" + this.roundedSubtotal);
  }

  onIncrease = item => {
    this.props.onIncrease(item);
    this.setState({
      subtotal: this.props.contents.reduce((acc, curr) => {
        return acc + curr.quantity * curr.price;
      }, 0)
    });
  }

  onDecrease = item => {
    this.props.onDecrease(item);
    this.setState({
      subtotal: this.props.contents.reduce((acc, curr) => {
        return acc + curr.quantity * curr.price;
      }, 0)
    });
  }

  roundedSubtotal = () => {
    let n = this.state.subtotal;
    return n.toFixed(2);
  }

  render() {
    return (
      <div className="cart">
        <h3>Shopping Cart</h3>
        <ProductGrid isInCart list={this.props.contents} onSubmit={this.props.onSubmit} onIncrease={this.onIncrease} onDecrease={this.onDecrease} />
        {/* {this.props.contents.length > 0 ? this.props.contents.reduce((acc, item) => console.log(acc)) : `$0.00`} */}
        <Navbar className="bg-light justify-content-between" sticky="bottom">
          <Navbar.Brand>Subtotal: ${this.roundedSubtotal}</Navbar.Brand>
          <Button onClick={this.onCheckout}>Checkout</Button>
        </Navbar>
      </div>
    )
  }
}

export default Cart;
