import React from "react";
import "../css/Shop.css";
import ProductGrid from "./ProductGrid";
import Cart from "./Cart";

class Shop extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      cartContents: [],
      size: "Size",
      light: "Light",
      sort: undefined,
    };
  }

  onSelectFilterTypeSize = event => {
    this.setState({size:event})
  };

  onSelectFilterTypeLight = event => {
    this.setState({light:event})
  };

  sortAsc = (a,b) => (a-b)? 1 : -1
  sortDesc = (a,b) => (b-a)? 1 : -1

  onSelectFilterTypeLight = () => {
    if (this.state.sort === undefined) {
      this.setState({sort:this.sortAsc})

    } else {
      this.setState({sort:undefined})
    }

  };


  addToCart = (item) => {
    if (this.state.cartContents.some(el => item.name === el.name)) {
      let index = this.state.cartContents.findIndex(el => el.name === item.name);
      let updatedContents = [...this.state.cartContents];
      updatedContents[index] = { ...updatedContents[index], quantity: parseFloat(updatedContents[index].quantity) + parseFloat(item.quantity) }
      this.setState({ cartContents: updatedContents });
    } else {
      this.setState(state => ({
        cartContents: [...state.cartContents, item]
      }))
    }
  }

  removeFromCart = (item) => {
    this.setState(state => ({
      cartContents: state.cartContents.filter(el => el.name !== item.name)
    }))
  }

  render() {
    return (
      <div className="wrapper">
        <div className="cards-container">

          <ProductGrid list={this.props.list} isInCart={false} onSubmit={this.addToCart} />
        </div>
        <div className="cart-container">
          <Cart contents={this.state.cartContents} onSubmit={this.removeFromCart} />
        </div>
      </div>
    )
  }
}

export default Shop;
