import React from "react";
import Card from "react-bootstrap/Card";
import FormControl from "react-bootstrap/FormControl";
import Button from "react-bootstrap/Button";
import "../css/ProductCard.css";

class ProductCard extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      quantity: this.props.quantity 
    };
  }

  onClickButton = () => {
    // this.props.onSubmit();
    this.props.onSubmit({ name: this.props.name, quantity: this.props.quantity, price: this.props.price, size: this.props.size, light: this.props.light, img: this.props.img });

  }

  onIncrease = () => {
    this.setState({quantity: this.state.quantity + 1});
  }
  onDecrease = () => {
    this.setState({ quantiy: this.state.quantity - 1 });
  }

  render() {
    return (
      <Card className="card">
        <Card.Img variant="top" src={this.props.img} />
        <Card.Body>
          <Card.Text>{this.props.name}</Card.Text>
          <Card.Title> ${this.props.price} </Card.Title>
          <Card.Text>üìè {this.props.size}</Card.Text>
          <Card.Text>‚òÄÔ∏è {this.props.light}</Card.Text>
          {this.props.isInCart ? 
            <div>
              <div className="def-number-input number-input">
                {this.state.quantity > 1 ? 
                <button onClick={this.onDecrease} className="minus"></button>
                :
                <button className="no-minus" disabled={true}></button>
                }
                <input className="quantity" name="quantity" value={this.props.quantity} type="number" onChange={event => this.setState({ quantity: event.target.value })}/>
                <button onClick={this.onIncrease} className="plus"></button>

              </div>
              {/* Quantity: {this.props.quantity} */}
              <Button onClick={this.onClickButton} variant="primary">Remove</Button>
            </div> 
            : 
            <div>
              {/* <FormControl
              type="number"
              aria-label="Remove button"
              aria-describedby="basic-addon2"
              onChange={event => this.setState({ quantity: event.target.value })}/>  */}
            <Button onClick={this.onClickButton} variant="primary">Add to Cart</Button>
            </div>}

        </Card.Body>
      </Card>
    )
  }
}

export default ProductCard;
