import React from "react";
import "../css/Cart.css";
import { Button, Navbar } from "react-bootstrap";
import ProductGrid from "./ProductGrid";

/**
 * This class handles calculating the total price of all the items in the cart. 
 */
class Cart extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      contents: this.props.contents,
      total: 0,
    };
  }

  /**
   * This function ensures that the Cart's total is updated the instant an item is added to the Cart. 
   */
  componentDidUpdate(prevProps, prevState) {
    if (prevProps.contents !== this.props.contents || prevState.contents !== this.state.contents) {
      this.setState({ contents: this.props.contents });
      this.setState({
        total: this.props.contents.reduce((acc, curr) => {
          return acc + curr.quantity * curr.price;
        }, 0)
      });
    }
  }

  /**
   * Creates an alert of the total price when the user clicks the Checkout button. 
   */
  onCheckout = () => {
    alert("Checkout Total: $" + (this.state.total).toFixed(2));
  }

  /**
   * Recalculates the Cart's total price when the user increases the item's quantity. 
   */ 
  onIncrease = item => {
    this.props.onIncrease(item);
    this.setState({
      total: this.props.contents.reduce((acc, curr) => {
        return acc + curr.quantity * curr.price;
      }, 0)
    });
  }

  /**
   * Recalculates the Cart's total price when the user decreases an item's quantity. 
   */
  onDecrease = item => {
    this.props.onDecrease(item);
    this.setState({
      total: this.props.contents.reduce((acc, curr) => {
        return acc + curr.quantity * curr.price;
      }, 0)
    });
  }

  render() {
    return (
      <div className="cart">
        <h3>Shopping Cart</h3>
        {/* For items in the Cart, pass the list of contents, addToCard function, and removeFromCart function from Shop to ProductGrid. This also pass the total price recalulations on increasing and decreasing quantity to ProductGrid. */}
        <ProductGrid isInCart={true} list={this.props.contents} onSubmit={this.props.onSubmit} onIncrease={this.onIncrease} onDecrease={this.onDecrease} />
        <Navbar className="bg-light justify-content-between" sticky="bottom">
          <Navbar.Brand>Total: ${(this.state.total).toFixed(2)}</Navbar.Brand>
          <Button onClick={this.onCheckout}>Checkout</Button>
        </Navbar>
      </div>
    )
  }
}

export default Cart;
