import React from "react";
import "../css/Shop.css";
import ProductGrid from "./ProductGrid";
import Cart from "./Cart";
import Header from "./Header"

class Shop extends React.Component {
  constructor(props) {
    super(props);
    //The state is just a list of key/value pairs (like a hashmap)
    this.state = {
      cartContents: [],
      size: "Size",
      light: "Light",
      sortPrice: undefined,
      priceTitle: "Select",
      total: 0
    };
  }

/*   Called every time a new Size filter type is selected
 */  onSelectFilterSize = event => {
    this.setState({ size: event })
  };

/* Called every time a new Lighting filter type is selected
 */  onSelectFilterLight = event => {
    this.setState({ light: event })
  };

/*   Determines whether the item being passed in matches the Size type that we are filtering on
 */  matchesFilterSize = item => {
    if (this.state.size === "Size") {
      return true
    } else if (this.state.size === item.size) {
      return true
    } else {
      return false
    }
  }

/*   Determines whether the item being passed in matches the Lighting type that we are filtering on
 */  matchesFilterLight = item => {
    if (this.state.light === "Light") {
      return true
    } else if (this.state.light === item.light) {
      return true
    } else {
      return false
    }
  }

  /*  Passed into a builtin filter() function. filter() calls this function on every element
    in the list. If this function returns true for a given element, filter() will add that element to its
    return list, which is the list of filtered plants.   */
  filterItems = item => {
    return this.matchesFilterSize(item) && this.matchesFilterLight(item);
  }

/*   Compares two plants in the list, and sorts them in price increasing order
 */  sortLowToHigh = (a, b) => (a.price > b.price) ? 1 : -1
/*   Compares two plants in the list, and sorts them in price decreasing order
 */  sortHighToLow = (a, b) => (a.price < b.price) ? 1 : -1

/*   Called when the "Lowest to Highest" price filter is selected in the dropdown
 */  onPriceLowToHigh = () => {
    let lowToHigh = "Lowest to Highest";
    this.setState({ sortPrice: this.sortLowToHigh });
    this.setState({ priceTitle: lowToHigh })
  }

  /* Called when the "Highest to Lowest" price filter is selected in the dropdown
   */
  onPriceHighToLow = () => {
    let highToLow = "Highest to Lowest";
    this.setState({ sortPrice: this.sortHighToLow });
    this.setState({ priceTitle: highToLow })
  }

  /*  Called when the price filter is reverted to "Select" in the dropdown
   */
  onPriceSelect = () => {
    let select = "Select";
    this.setState({ sortPrice: undefined });
    this.setState({ priceTitle: select })
  }


  /* This function is called when the user adds an item in the Cactus Shop list to the Shopping Cart. 
  */
  addToCart = (item) => {
    let updatedContents;
    // The some() method checks if any of the elements in an array pass a test (provided as a function). Here, the checking condition is whether the name of the added item matches an existing index in the updatedContents list, which is the Cart.
    if (this.state.cartContents.some(el => item.name === el.name)) {
      let index = this.state.cartContents.findIndex(el => el.name === item.name);
      // If it is true, then the old cartContents list is copied to the updatedContents list.
      updatedContents = [...this.state.cartContents];
      // We also update the located index in the updatedContents list with the item's name and new quantity.
      updatedContents[index] = { ...updatedContents[index], quantity: parseFloat(updatedContents[index].quantity) + parseFloat(item.quantity) }
      this.setState({ cartContents: updatedContents });
    } else {
      // If the item is not in the Cart, copy the old cartContents and add the item to the updatedContents list
      updatedContents = [...this.state.cartContents, item];
      // Set the Cart to the updatedContents
      this.setState({
        cartContents: updatedContents
      })
    }
    // Calculate the total price of the items in the Cart
    this.calculateTotal(updatedContents);
  }

  /* This function removes an item from the Cart by filtering the cartContents to only render items other than the removed item.
  */
  removeFromCart = (item) => {
    const updatedContents = this.state.cartContents.filter(el => el.name !== item.name);
    this.setState({
      cartContents: updatedContents
    })
    this.calculateTotal(updatedContents);
  }

  /* Decreases the quantity of an item in the Cart when the user decrements the item's amount. */
  onDecrease = (item) => {
    const itemObject = this.state.cartContents.find(obj => obj.name === item);
    if (itemObject.quantity > 1) {
      const index = this.state.cartContents.indexOf(itemObject);
      this.state.cartContents[index].quantity = this.state.cartContents[index].quantity - 1;
    }
  }

    /* Increases the quantity of an item in the Cart when the user increments the item's amount. */
  onIncrease = (item) => {
    const itemObject = this.state.cartContents.find(obj => obj.name === item);
    const index = this.state.cartContents.indexOf(itemObject);
    this.state.cartContents[index].quantity = this.state.cartContents[index].quantity + 1;
  }

  calculateTotal = contents => {
    this.setState({
      total: contents.reduce(
        (accumulatedTotal, cartItem) =>
          accumulatedTotal + cartItem.price * cartItem.quantity,
        0
      )
    });
  }

  render() {
    return (
      <div className="wrapper">
        <div className="cards-container">
          {/* The size and lighting filters, price sort functions are passed to the Header */}
          <Header onSelectFilterSize={this.onSelectFilterSize} onSelectFilterLight={this.onSelectFilterLight} onPriceLowToHigh={this.onPriceLowToHigh} onPriceHighToLow={this.onPriceHighToLow} onPriceSelect={this.onPriceSelect} priceTitle={this.state.priceTitle} handlePriceTitle={this.handleSortPriceTitle} size={this.state.size} light={this.state.light} />

          {this.props.list.filter(this.filterItems).length > 0 ?
            <div>
              <ProductGrid list={this.props.list.filter(this.filterItems).sort(this.state.sortPrice)} isInCart={false} onSubmit={this.addToCart} />
            </div>
            :
            <div>
              <p>No results match the filters :(</p>
            </div>
          }
        </div>
        <div className="cart-container">
          <Cart contents={this.state.cartContents} subtotal={this.state.total} onSubmit={this.removeFromCart} onIncrease={this.onIncrease} onDecrease={this.onDecrease} />
        </div>
      </div>
    );
  }
}

export default Shop;
